import React from 'react';
import CartCard from '../utilities/CartCard';

const ShoppingCartPage = props => {
    const {cart} = props;

    function getSum() {
        let sum = 0;
        for (let item of cart.values()) {
            sum += item.quantity * item.price;
        }
        return sum.toFixed(2);
    }

    const items = [];
    for (let item of cart.values()) {
            items.push(item);
    }

    const cards = items.map(item => {
        return(
            item.quantity > 0 ?
                <CartCard 
                    key={item.id}
                    {...item}
                    reduceQuantity={props.reduceQuantity}
                    addQuantity={props.addQuantity}
                /> : null
        )
    })

    return(
        <div>
            {cart.size > 0 ? <div className='page content-page shopping-cart-page'>
                <div className='cart-info'>
                    <p className='cart-p'>{cart.size === 0 ? 'Your cart is empty!' : 'Your cart'}</p>
                    {cart.size > 0 && <div className='item-div'>
                        {cards}
                    </div>}
                    <p>{`Total: $${getSum()}`}</p>
                    <div className='shopping-cart-button-div'>
                        <button className='checkout-button'>Checkout</button>
                    </div>
                </div>
            </div> : <p>Your cart is empty!</p>}
        </div>
    )
}

export default ShoppingCartPage;
