import React from 'react';

const ShoppingCartPage = props => {
    const {cart, cartStatus, toggleCart} = props;

    const sum = cart.length > 0 ? cart.reduce((prevValue, currentValue) => prevValue + currentValue.price): 0;

    return(
        <div>
            {cartStatus && <div className='shopping-cart-div'>
                <p>Your Cart</p>
                <div className='button-div'>
                    <p>{`Total: ${sum}`}</p>
                    <button>Checkout</button>
                    <button onClick={toggleCart}>Close</button>
                </div>
            </div>} 
        </div>
    )
}

export default ShoppingCartPage;
