import React from "react";
import quotes from '../utilities/quotes';

const HomePage = () => {

    function getRandomQuote() {
        const randomIndex = Math.floor(Math.random() * quotes.length);
        return quotes[randomIndex];
    }
    return(
        <div className='page content-page home-page'>
            <div className='home-left'>
                <h3>Classic Middle Eastern Art</h3>
                <p className='quote-p'>{getRandomQuote().quote}</p>
                <button>View Collection</button>
            </div>
            <div className='home-right'>
                <img className='homepage-img' src={'./rainbow mosque.jpg'} alt='Rainbow Mosque'/>
            </div>
        </div>
    )
}

export default HomePage;
