import React from 'react';

const Card = (props) => {
    const {id, fileName, name, price, handleClick} = props;

    return(
        <div>
            <img src={`./${fileName}`} alt={`${name}`}/>
            <p>{name}</p>
            <p>{price}</p>
            <button onClick={() => handleClick(id)}>Add to cart</button>
        </div>
    )
}

export default Card;
