import React from "react";
import Navbar from "./components/Navbar";
import { Routes, Route } from "react-router-dom";
import HomePage from './components/pages/HomePage';
import CollectionPage from './components/pages/CollectionPage';
import ContactPage from './components/pages/ContactPage';
import collectionData from './components/utilities/collection data';
import ShoppingCartPage from "./components/pages/ShoppingCartPage";

function App() {
  const [cart, setCart] = React.useState(new Map());
  const [itemCount, setItemCount] = React.useState(-1);
  const [showCart, setShowCart] = React.useState(false);

  function addToCart(id) {
    const item = collectionData[id];
    setCart(prevCart => {
      const updatedCart = new Map(prevCart);
      updatedCart.get(item.id) ? updatedCart.get(item.id).quantity++ : updatedCart.set(item.id, {...item, quantity: 1});
      return updatedCart;
    })
  }

  React.useEffect(() => {
    setItemCount(prevCount => {
      let count = 0;
      for (let item of cart.values()) {
        count += item.quantity;
      }
      return count;
    });
  }, [cart]);

  function toggleCart() {
    setShowCart(prevState => !prevState);
  }

  function reduceQuantity(id) {
    const item = cart.get(id);
    if (item.quantity < 1) {
      return;
    }
    setCart(prevCart => {
      const updatedCart = new Map(prevCart);
      item.quantity--;
      updatedCart.set(id, item);
      return updatedCart;
    })
  }

  function addQuantity(id) {
    const item = cart.get(id);
    setCart(prevCart => {
      const updatedCart = new Map(prevCart);
      item.quantity++;
      updatedCart.set(id, item);
      return updatedCart;
    })
  }

  return (
    <div className='App'>
      <div>
        <Navbar itemCount={itemCount} toggleCart={toggleCart}/>
        <ShoppingCartPage cart={cart} cartStatus={showCart} toggleCart={toggleCart} reduceQuantity={reduceQuantity} addQuantity={addQuantity}/>
        <Routes>
          <Route path='/' element={<HomePage/>}/>
          <Route path='/Collection' element={<CollectionPage addToCart={addToCart}/>}/>
          <Route path='/Contact' element={<ContactPage/>}/>
        </Routes>
      </div>
    </div>
  );
}

export default App;
