import React from "react";
import Navbar from "./components/Navbar";
import { Routes, Route } from "react-router-dom";
import HomePage from './components/pages/HomePage';
import CollectionPage from './components/pages/CollectionPage';
import ContactPage from './components/pages/ContactPage';
import collectionData from './components/utilities/collection data';
import ShoppingCartPage from "./components/pages/ShoppingCartPage";

function App() {
  const [cart, setCart] = React.useState([]);
  const [showCart, setShowCart] = React.useState(false);

  function addToCart(id) {
    const item = collectionData[id];
    setCart(prevCart => {
      return [...prevCart, item];
    })
  }

  function toggleCart() {
    setShowCart(prevState => !prevState);
  }

  return (
    <div className="App">
      <Navbar cartLength={cart.length} toggleCart={toggleCart}/>
      <ShoppingCartPage cart={cart} cartStatus={showCart} toggleCart={toggleCart}/>
      <Routes>
        <Route path='/' element={<HomePage/>}/>
        <Route path='/Collection' element={<CollectionPage addToCart={addToCart}/>}/>
        <Route path='/Contact' element={<ContactPage/>}/>
      </Routes>
    </div>
  );
}

export default App;
