import React from 'react';
import Card from '../utilities/Card';

const ShoppingCartPage = props => {
    const {cart, cartStatus, toggleCart} = props;

    const sum = cart.length > 0 ? cart.reduce((prevValue, currentValue) => prevValue + currentValue.price, 0) : 0;

    // set array to the max number of unique purchaseable items
    const items = [18];
    for (let i = 0; i < cart.length; i++) {
        // place items from cart into items array based on the item's id
        if (items[cart[i].id]) {
            items[cart[i].id].quantity++;
        } else {
            const addedItem = cart[i];
            addedItem['quantity'] = 1;
        }
    }

    return(
        <div>
            {cartStatus && <div className='shopping-cart-div'>
                <p>Your Cart</p>
                <div>

                </div>
                <div className='button-div'>
                    <p>{`Total: $${sum}`}</p>
                    <button>Checkout</button>
                    <button onClick={toggleCart}>Close</button>
                </div>
            </div>} 
        </div>
    )
}

export default ShoppingCartPage;
